query.q:
  sum by (instance) (
    irate(
      node:cpu_seconds_total{instance="#{node}",job="#{job}",mode!="idle",mode!="iowait",mode!="irq",mode!="softirq",mode!="system",mode!="user"}
    )
  )
/ on (instance) group_left ()
  sum by (instance) ((irate(node:cpu_seconds_total{instance="#{node}",job="#{job}"})))
